mapper.select:
		select *
      	from user_info 
		where user_type = #{user_type} and user_auth = #{user_auth}

mapper.update:
update 	user_info 
set 	user_nickname = #{user_nickname},
   	   	user_match = #{user_match},
   	   	user_hate = #{user_hate}
where user_type = #{user_type} and user_auth = #{user_auth}

mapper.delete:
delete 
from	user_info
where 	user_type= #{user_type} and user_auth = #{user_auth}
